import React, { Component } from 'react';
import ReactTable from "react-table";
import "react-table/react-table.css";
import { Line, Bar, LinePath } from "@vx/shape";

import { localPoint } from "@vx/event";
import { extent, max, bisector } from "d3-array";
import {timeFormat} from "d3-time-format"

import { scaleTime, scaleLinear } from "@vx/scale";

import { withTooltip, Tooltip } from "@vx/tooltip";

// in render


const width = window.innerWidth-300;
const height = window.innerHeight-800;

const xSelector = d => new Date(d.date);
const ySelector = d => d.price;

const bisectDate = bisector(xSelector).left;

const formatDate = timeFormat("%b %d, '%y");

class Chart extends Component {
    state = {
        data: null
    };

    async componentDidMount() {
        const res = await fetch("https://api.coindesk.com/v1/bpi/historical/close.json");
        const data = await res.json();
        this.setState({
            data: Object.keys(data.bpi).map(date => {
                      return {
                          date,
                          price: data.bpi[date],
                      };
                  })
        })
        console.log(data);
    }

    handleTooltip = ({ event, data, xSelector, xScale, yScale }) => {
        const { showTooltip } = this.props;
        const { x } = localPoint(event);
        const x0 = xScale.invert(x);
        const index = bisectDate(data, x0, 1);

        const d0 = data[index - 1];
        const d1 = data[index];
        let d = d0;
        if (d1 && d1.date) {
          d = x0 - xSelector(d0) > xSelector(d1) - x0 ? d1 : d0;
        }

        showTooltip({
          tooltipData: d,
          tooltipLeft: xScale(xSelector(d)),
          tooltipTop: yScale(ySelector(d)),
        });
      };

    render() {
        this.state.data = componentDidMount();
        const { showTooltip, hideTooltip, tooltipData, tooltipTop, tooltipLeft } = this.props;
        const { data } = this.state;
        if (!data) return null;

        const padding = 100;
        const xMax = width - padding;
        const yMax = height - padding;

        const maxData = max(data, ySelector);
        const yScale = scaleLinear({
            range: [yMax, padding],
            domain: [0, maxData + (maxData/3)],
        })

        const xScale = scaleTime({
              range: [padding, xMax],
              domain: extent(data, xSelector),
        });
        return (

            <div>
                <svg width={width} height={height}>
                    {tooltipData && (
                      <g>
                        <Line
                          from={{ x: tooltipLeft, y: 0 }}
                          to={{ x: tooltipLeft, y: yMax }}
                          stroke="#5C77EB"
                          strokeWidth={4}
                          style={{ pointerEvents: "none" }}
                          strokeDasharray="4,6"
                        />
                        <circle
                          cx={tooltipLeft}
                          cy={tooltipTop}
                          r={4}
                          fill="#5C77EB"
                          stroke="white"
                          strokeWidth={2}
                          style={{ pointerEvents: "none" }}
                        />
                      </g>
                    )}
                    <rect x={0} y={0} width={width} height={height} fill="#32deaa" />
                    <LinePath
                      data={data}
                      xScale={xScale}
                      yScale={yScale}
                      x={xSelector}
                      y={ySelector}
                      strokeWidth={5}
                      stroke="#FFF"
                      strokeLinecap="round"
                      fill="transparent"
                    />
                    <Bar
                        x={0}
                        y={0}
                        width={width}
                        height={height}
                        fill="transparent"
                        data={data}
                        onMouseMove={data => event => this.handleTooltip({
                                                              event,
                                                              data,
                                                              xSelector,
                                                              xScale,
                                                              yScale,
                                                            })}
                        onMouseLeave={data => event => hideTooltip() }
                      />
                </svg>
            </div>
        )
    }
}

export default withTooltip(Chart);
