import React, { Component } from 'react';
import ReactTable from "react-table";
import "react-table/react-table.css";
import * as datetime from "react-datetime";
import * as moment from "moment";


class TransactionTable extends Component {

    /*
    state = {
        columns: ['Coin', 'Quantity', 'Value (USD)'],
        rows: [{
            'Coin': 'ETH',
                'Quantity': 5000,
                'Value (USD)': 60000
              }, {
                'Coin': 'XBP',
                'Quantity': 50,
                'Value (USD)': 2000
              }
        ]
    }
    */

    state = {
            columns: [{Header: 'Coin', accessor: 'coin', width: 50}, {Header: 'Quantity', accessor: 'quantity', width: 100}, {Header: 'Price', accessor: 'price', width: 100}, {Header: 'Date', accessor: 'date', width: 400}],
            rows: [{
                'coin': 'ETH',
                    'quantity': 5000,
                    'value': 60000,
                    'price': 100,
                    'date':  "2012-04-23T00:00:00.000Z"
                  }, {
                    'coin': 'XBP',
                    'quantity': 50,
                    'value': 2000,
                    'price': 100,
                    'date': 1/2/2019
                  }, {
                    'coin': 'XAE',
                    'quantity': 50,
                     'value': 200,
                     'price': 100,
                     'date': 1/2/2019
                  }
            ]
        }

    /*
    render() {

        var dataColumns = this.state.columns;
        var dataRows = this.state.rows;

        var tableHeaders = (<thead className="thead-dark" ><tr>{dataColumns.map(function(column) {return <th>{column}</th>; })}</tr></thead>);
        var tableBody = dataRows.map(function(row) {
            return (<tr>{dataColumns.map(function(column) { return <td>{row[column]}</td>; })}</tr>); });


        return (<table className="table table-striped width=50%">
                {tableHeaders}
                {tableBody}
              </table>);
    }
    */
    formatDateTime() {
        let m = moment.format('LLLL');
        return m;
    }

    render() {
        let now = moment().format('LLLL');
        return (
            <div>
                <span className="badge m-2 badge-primary">These are your most recent transactions</span>
                <ReactTable
                    data = {this.state.rows}
                    columns= {this.state.columns}
                    defaultPageSize = {8}
                    style={{height: '300px', width: '500px'}}
                    className="-striped -highlight"
                    showPagination={false}
                />
            </div>
        )
    }

}

export default TransactionTable;
