import React, { Component } from 'react';
import ReactTable from "react-table";

class Table extends Component {

    state = {
        columns: ['Coin', 'Quantity', 'Value (USD)'],
        rows: [{
            'Coin': 'ETH',
                'Quantity': 5000,
                'Value (USD)': 60000
              }, {
                'Coin': 'XBP',
                'Quantity': 50,
                'Value (USD)': 2000
              }
        ]
    }

    /*
    state = {
            columns: ['Coin', accessor: 'coin'}, {Header: 'Quantity', accessor: 'quantity'}, {Header: 'Value (USD)', accessor: 'value'}],
            rows: [{
                'coin': 'ETH',
                    'quantity': 5000,
                    'value': 60000
                  }, {
                    'coin': 'XBP',
                    'quantity': 50,
                    'value': 2000
                  }
            ]
        }
        */

    render() {

        var dataColumns = this.state.columns;
        var dataRows = this.state.rows;

        var tableHeaders = (<thead className="thead-dark width= 50%" ><tr>{dataColumns.map(function(column) {return <th>{column}</th>; })}</tr></thead>);
        var tableBody = dataRows.map(function(row) {
            return (<tr>{dataColumns.map(function(column) { return <td>{row[column]}</td>; })}</tr>); });


        return (<table className="table table-striped width=50%">
                {tableHeaders}
                {tableBody}
              </table>);
    }

    /*
    render() {
        return (
            <div>
                <ReactTable
                    data = {this.state.rows}
                    columns= {this.state.columns}
                    defaultPageSize = {3}
                    pageSizeOptions = {[3,6]}
                />
            </div>
        )
    }
    */
}

export default Table;
