import React, { Component } from 'react';
import ReactTable from "react-table";
import "react-table/react-table.css";
import "./component.css"
import Fade from 'react-reveal/Fade';

class InputForm extends Component {
    state = {
        bid: null,
        token: null,
        trade_in: null,
        quantity: null,
        price: null
    }
    constructor(props) {
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.state = {
            bid: null,
            token: null,
            trade_in: null,
            quantity: null,
            price: null
        }
    }
    handleSubmit = (event) => {
        event.preventDefault()
    }

    handleBuyChange = (event) => {
        console.log(event)
        console.log(event.target.name)
        console.log(event.target.value)
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    handleSellChange = (event) => {
            console.log(event)
            console.log(event.target.name)
            console.log(event.target.value)
            this.setState({
                [event.target.name]: event.target.value
            })
        }
    render() {
        return (
            <div className="forms">
                <div>
                <span onClick={this.handleClick} className="buy-badge"> BUY </span>
                <form className="buy-form" onSubmit={this.handleSubmit}>
                    <p><input type='text' placeholder="token" name='buy' onChange={this.handleBuyChange}/></p>
                    <p><input type='text' placeholder="trade for" name='buy' onChange={this.handleBuyChange} /></p>
                    <p><input type='number' placeholder="amount" name='buy' onChange={this.handleBuyChange} /></p>
                    <p><input type='number' placeholder="bid price" name='buy' onChange={this.handleBuyChange} /></p>
                    <p><button className="buy-button-badge"> Bid </button></p>
                </form>
                </div>
                <div>
                    <span onClick={this.handleClick} className="sell-badge"> SELL </span>
                    <form className="sell-form" onSubmit={this.handleSubmit}>
                        <p><input type='text' placeholder="token" name='buy' onChange={this.handleSellChange} /></p>
                        <p><input type='text' placeholder="trade for" name='buy' onChange={this.handleSellChange} /></p>
                        <p><input type='number' placeholder="amount" name='buy' onChange={this.handleSellChange} /></p>
                        <p><input type='number' placeholder="ask price" name='buy' onChange={this.handleSellChange} /></p>
                        <p><button className="sell-button-badge"> Ask </button></p>
                    </form>
                </div>
            </div>
        )
    }
}

export default InputForm
